import { useState } from "react";

function Guest(props){
    const [checkedIn, setCheckedIn] = useState("❌");

    function handleCheckIn(){
        var status = checkedIn === "❌"? "✅" : "❌" 
        setCheckedIn(status)
      }

    return (
        <>
        <tr>
               <td>{props.name}</td>
               <td>{props.age}</td>
               <td>{checkedIn}</td>
               <td><button onClick={handleCheckIn}>Check in</button></td> 
              </tr></>
    )
}

export default Guest;
