import './App.css';
import pg from "pg";


function App() {
  const db = new pg.Client({
    user: "postgres",
    host: "localhost",
    database: "invitation-manager",
    password: "Bl@cK200",
    port: 5432,
  });

  db.connect()

  let people = {};
  db.query("SELECT * FROM people_invited", (err, res) => {
    if (err) {
        console.error("Error executing query", err.stack);
    } else {
        people = res.rows;
    }
});

  console.log(people);

  return (
    <div className="App">

    </div>
  );
}

export default App;
