import { useEffect, useState } from 'react';
import axios from 'axios';
import Guest from '../components/Guest';

function GuestList(){

    const [guests, setGuests] = useState([]);
    const [statusUpdate, setSattusUpdate] = useState({

    })

  useEffect(() => {
    fetchData()
  }, [guests]);

  let fetchData = async () => {
    const response = await axios.get(
      "http://localhost:8080/people"
    );

    setGuests(response.data);
  }

  function changeStatus(guest){
    axios.put('http://localhost:8080/people/'+guest.id, {
        id: guest.id,
        name: guest.name,
        age: guest.age,
        present: guest.present === 'no'? 'yes': 'no'
      })
  }

  return(
    <div className="container">
      <div className='container'>
        <h1>Lista de Convidados</h1>
      </div>
      <div>
      <table className='table-container'>
        <tr>
          <th>Nome</th>
          <th>Idade</th>
          <th>Presente</th>
          <th></th>
        </tr>

      {guests.map(function(item){
        return <Guest key={item.id} name={item.name} age={item.age} checkedIn={item.present === "yes"? "✅" : "❌"} handleCheckin={changeStatus(item)}/>
      })}
      </table>
      </div>

    </div>
  )


}

export default GuestList;
